    private void btnIconoLupaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnIconoLupaActionPerformed
        nControlA = txtFNControl.getText();

        if (!nControlA.isEmpty()) {
            String nControlExisteSQL = "SELECT Numero_Control FROM Alumno WHERE Numero_Control =" + nControlA;
            if (fn.verificarExistencia(nControlExisteSQL) == true) {
                obtenerDatosA();
                obtenerDatosM();
                cargarDatosA();
                cargarDatosM();
                txtFNControl.setEnabled(false);
                btnIconoLupa.setEnabled(false);
                btnConfirmar.setEnabled(true);
                btnConfirmar.setVisible(true);
                btnCancelar.setEnabled(true);
                btnCancelar.setVisible(true);
            } else {
                mostrarError("Alumno NO Encontrado", "El n√∫mero de control no existe o no pertenece a un alumno");
            }
        } else {
            mostrarError("Campo vacio", "Escribe un numero de control");
        }
    }//GEN-LAST:event_btnIconoLupaActionPerformed

